CREATE TABLE `hot_topic` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `topic_id` varchar(40) COLLATE utf8_unicode_ci DEFAULT NULL,
  `topic_title` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `toppic_content` varchar(2000) COLLATE utf8_unicode_ci DEFAULT NULL,
  `answer_num` int(20) DEFAULT '0',
  `topic_read` int(20) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=49 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
-- Records of hot_topic
-- ----------------------------
BEGIN;
INSERT INTO `hot_topic` VALUES (1, 'ff84c118f2a8433d922dfbe45e7df8a8', '#疫情下劳动用工指南#公司提前复工，HR和员工该怎么办？', '疫情期间，公司的一举一动，最见一家公司的格局。在大多数公司都延迟复工的时候，也有不少的公司，仍然按照原来的计划复工，甚至在延长春节假期的政策出台后，要求提前复工。|有的人会觉得，公司的这种规定，实在让人寒心。当我们真正遇到的时候，辞职不去了吗？|毕竟除了涉及保障疫情防控地区、群众生活必要的、涉及国计民生的相关企业需要提前复工外，其他企业提前复工的是少之又少。如果真的需要提前复工，HR和员工，需要做好下面几点。|对于HR来说：|第一、务必和公司领导进行详细沟通。|最好结合当前国务院发布的延长春节假期政策，以及不遵守政策后可能带来的法律责任，需要对其进行详细解释。如果沟通完毕，领导们考虑到公司运营成本和生产成本，还是决定要提前复工，那么HR就要做好下一步通知到员工的准备。|第二、务必将此次公司提前复工的决定及时通知到员工个人，并做好相应的请假考勤制度。|考虑到有些员工可能会以请假来延长到岗时间，所以这部分的规定一定要和员工沟通好，避免未能及时保障员工权益。|第三、HR需要及时为复工期间的疫情防护做好准备。|像一些基本的防护物资，诸如消毒水、口罩、体温计等，都需要提前准备就绪。虽然现在对于口罩是否由公司来提供，存在不少的议论。但是出于疫情期间安全的考虑，公司准备口罩也是应该的，试想一下员工未带口罩感染疫情，那么HR心里会不会觉得这是自己的一次失误呢？疫情出现后是不是会给公司带来更大的损失呢？|虽然有部分网友觉得戴口罩是员工个人的事情，但是本着疫情防护优先原则，公司HR准备一些口罩，也是很有必要的。|第四、复工期间做好员工的身体检查工作。|比如每天分三次对员工的体温进行检测，及时登记备案。还要做好危机处理预案，假如真的发现员工出现发热、咳嗽等症状，HR至少要知道接下来该怎么做，不至于手忙脚乱，延误疫情的紧急处理时间。|对于员工来说：|第一、接到公司提前复工的通知后，需要对当前的情况做一个综合判断。|结合自己的家庭和周围环境以及疫情发展情况综合得出结论。|假如你所在的城市疫情不严重，那么你完全可以在做好防护措施的情况下，前往公司上班。如果你所在城市为武汉，在现在这么严峻的形势下，你就完全可以考虑请假或者是辞职在家等待疫情不严重了再做打算。|毕竟生命安全，是最后的底线！|第二、员工复工，需要提高自我的疫情防护意识。|生命是自己的，在整个抗病毒的大环境下，员工对于自己的生命安全，需要更为重视。那就需要树立疫情防护意识，学习疫情防护知识，实践疫情防护方法。经常洗手、戴口罩等必须的防护措施，员工不管是在家里还是在公司，都是需要时刻谨记的。|第三、员工需要配合HR的工作。|疫情期间情况特殊，员工更要配合好HR的相关工作，比如真实汇报自己的身体状况，遇到任何困难及时和HR取得联系，做到信息沟通的及时准确完整。|针对提前复工的注意事项，HR和员工都是要时刻牢记“安全”两个字。', 0, 3);
INSERT INTO `hot_topic` VALUES (2, '1A099883184B4FCEB18BD049CE82A5B1', '面试谈薪资遭遇HR压价，该坦然接受吗？', '这个涉及到契约精神和企业的价值观，我18年去某元证券去面试、复式，什么都谈好了，入职签合同时某元证券的负责人临时提出降薪，最后我没妥协，果断的放弃了，事实证明，后续那边的hr也离职了，还有一些人，所以一个企业的契约精神很重要，这是红线。', 0, 13);
INSERT INTO `hot_topic` VALUES (3, '1A099883184B4FCEB18BD049CE82A5B2', '疫情下公司经营困难，要坚持到最后吗？', '不坚持 这个时候就是可以看出一个公司企业文化，员工忠诚度的时候了，如果这个时候公司人离职就多，那大多数情况员工是不认可公司企业文化的，否则员工会陪公司渡过难关，毕竟这个应该一年内可以得到恢复', 0, 3);
INSERT INTO `hot_topic` VALUES (4, 'DECA8ABAD21D45958AAA9B5ACFB15721', '#职场问答PK广场#欧洲股市全线暴跌，你的收入会受股市影响而减少吗？', '难过，亏大发了 跳楼，有一起的吗', 0, 3);
INSERT INTO `hot_topic` VALUES (5, '1A099883184B4FCEB18BD049CE82A5B1', '你因为哪首歌爱上旅行团？【抽联名款围巾】', '以前还没听过这个乐队，特地跑去听了下哈。|曾经被别人感染成为了一个“名字”控。|《岛屿》这首歌她应该喜欢，普通事物的旧词新解。|人很奇怪，在特定情绪下读诗、听歌都会有特别的感悟，那些一瞬间读懂一个诗人，爱上一个乐队的时候，忸怩造作的情绪似乎有了一个切合点，一定都不容易吧。', 0, 0);
INSERT INTO `hot_topic` VALUES (6, '1A099883184B4FCEB18BD049CE82A5B2', '疫情结束后，会爆发求职狂潮吗？', '会。|1.年前已经离职的，早就在摩拳擦掌了，本想在年后找份工作，碰到疫情，疫情过了这些没有工作的会更迫切需要一份工作；|2.目前虽然还在值，中小企业遭受重创，会从福利待遇上节流，对于员工来说，还是更愿意去找没有受影响的，这部分人会选择跳槽；|3.还有一大批应届生，准备蜂拥而至。|妈呀，压力好大～', 0, 0);
INSERT INTO `hot_topic` VALUES (7, '1A099883184B4FCEB18BD049CE82A5B3', '视频面试需要注意什么？', '参加过两次视频面试，和普通面试比起来需要多注意一下网络环境，保证高质量的网络。第二就自身所在环境整洁，噪音小。第三就是可以临时改变一下昵称和头像之类的app信息，实名正装照可能比较好。第四就是回答问题时候要等面试官的问题提出完整后再作答，不要因为网络延迟等原因抢话。其他的还比较好，甚至可以准备一些资料在手边，面试的时候可以搂两眼。', 0, 0);
INSERT INTO `hot_topic` VALUES (8, '25e6ad1e1d1c4d2c939bdb89e2a96d64', '#疫情下劳动用工指南#肺炎疫情下，对于HR招人到底有没有影响？', '对社招影响不大，短期对校招的影响比较大，当然，咱还是要相信咱强大的政府，我先简单的说一下校招这块，安静的看，不要激动（个人对金融方向比较了解，其他方向自己看是否合适参考）。|首先，线下校园招聘，春招这块肯定是比较直接的影响了。|本来正月十五后，大部分企业就陆续开启校招，包括校园宣讲，线下双选会，目前这块肯定是受到了直接的影响，别说学校这块短期开学时间暂定，即便是开学了，这种百人宣讲也会把控的比较严格。这点，对于部分不错学校影响最为直接，可以明显的感觉到这块的变化，所以秋招没有签兜底offer的同学，相对比较忧伤。其次，线下面试及笔试这块，影响也是很现实。校园招聘不是单纯的一对一面试，很多一面都是群面，试问，现在合适把大家聚集到一起进行群面么?试问，现在适合组织校招笔试么？你也许会说，改一对一啊，这个，需要时间精力吧。短期看，这块影响也是比较直接的。|此外，说一下今年的考研，今年考研人数是绝对的上了新高，这个新高意味着非常多的人是考不上要走校招的，其实这本身就意味着这个群体在春招时候的压力非常的大。但没办法，短期是很难让大家去冲刺了，这群人的特点是对校招还没啥认知。|给大家个建议，与其在这担心，还不如提前准备准备笔试、面试，要准备的东西，其实是非常的多。最后，说一下到底影响有多大，短期影响肯定是挺大，但长期看影响不大，该用工的还是需要用工，该招聘的还是需要招聘，只不过短期几个月，大家就业的压力会大非常多。到这里，需要告诉大家，完全不需要慌，你慌也没有用，就推迟的问题。当然，能力好的完全不需要担心，只要有一些机会，比较快就能抓住。如果是校招小白，那就可能比较煎熬了，加油吧。|社招简单说一下吧，年后的岗位还是比较多的，每年都是这个情况，大家拿了奖金后跳槽。但今年，个人建议不是非要离职的就别离职了，由于题中事件的影响，不少人短期基本就是拿个基础工资，少部分企业受到的影响比较重，不少跳槽的有还可以的工作没法太挑，大部分人还是停不了太久（缺钱，一段时间折腾下来）。所以，即便你离职了，大部分人很快就不挑，你要挑，那你就继续找吧，折腾一圈下来，还不如建议你不跳（这里主要指的是钱给的少想跳槽的，心委屈想跳槽的，那就自己倒腾吧）。|多充电吧，等待没有啥实质作用，机会来了，你也不一定抓的住，找工作，关键还是看你个人能力如何，你有能力，不会缺机会的，加油~都会越来越好的', 0, 1);
INSERT INTO `hot_topic` VALUES (9, '3CE4F0D0614D47898B525D0E22FFEFFA', '#疫情下劳动用工指南#面对疫情公司遇到开工难题，作为HR，该如何权衡员工与公司之间的复工安排呢？', '第一、务必和公司领导进行详细沟通。|第二、务必将此次公司提前复工的决定及时通知到员工个人，并做好相应的请假考勤制度。|考虑到有些员工可能会以请假来延长到岗时间，所以这部分的规定一定要和员工沟通好，避免未能及时保障员工权益。|第三、HR需要及时为复工期间的疫情防护做好准备。|像一些基本的防护物资，诸如消毒水、口罩、体温计等，都需要提前准备就绪。虽然现在对于口罩是否由公司来提供，存在不少的议论。但是出于疫情期间安全的考虑，公司准备口罩也是应该的，试想一下员工未带口罩感染疫情，那么HR心里会不会觉得这是自己的一次失误呢？疫情出现后是不是会给公司带来更大的损失呢？', 0, 1);
INSERT INTO `hot_topic` VALUES (10, '5487FE179DE24E9EB80196F0343E54AE', '#疫情下劳动用工指南#企业主动继续停工，2 月份工资和 3 月份工资怎么计算？', '根据《工资支付暂行规定》规定，用人单位停工停产在一个工资支付周期内的，用人单位应按劳动合同规定的标准支付职工工资。超过一个工资支付周期的，若职工提供了正常劳动，用人单位支付给职工的工资不得低于当地最低工资标准。职工没有提供正常劳动的，用人单位应当发放生活费，生活费标准按各省、自治区、直辖市规定的办法执行。| ', 0, 1);
INSERT INTO `hot_topic` VALUES (11, 'CB313975E7D14A699C28B694793A8A93', '#视频面试#hr打电话了解一些情况后，然后好几天不联系，是不是被pass了？', '这样公司就是完成任务，没有诚信的，我上次也是这样，面试的是老总，面试完老总说等我电话，一直都没有电话，我认为既然作为公司老总说了等电话，不管录取不录取都要给对方电话，而不是把作为拒绝别人，委婉拒绝应聘者的方法也不要把作为公司拒绝应聘者手段，企业要有诚信，像这样的企业我是不会去的，没诚信的企业没有前途。而且这样企业以前还找过我办事，你说这个企业还有必要去么，', 0, 0);
INSERT INTO `hot_topic` VALUES (12, '3CE4F0D0614D47898B525D0E22FFEFFA', '#疫情下劳动用工指南# 因封路封城等无法按时复工，企业可否让员工远程办公?不服从的，可否处罚?', '企业可根据员工提供的证明作出不同决定;具体而言，如员工 根据规章制度提供医院证明，此时公司应给予病假;如员工无法定理由或病假证 明，企业有权不批准事假、不安排年休假，可安排其远程办公，此时按照正常工 作支付工资。|如封路封城，且又没有政府延迟复工等相决定时，企业可正常安排远程办公，此 时按照正常工作支付工资;员工不服从的，虽不能按旷工按处理，但可按不服从 管理安排依据规章制度进行处理，不符合部分薪酬发放条件的，企业有权依据薪 资制度执行。', 0, 0);
INSERT INTO `hot_topic` VALUES (13, 'E9AC3AF09293473CA9CC21BC5C2EFED9', '#HR社区#房产销售不注重工作安排，只在意最终业绩。这个观念是对的吗？', '个人对房产业不是很了解，但是销售应该是个比较灵活的岗位，业绩才是最重要的', 0, 1);
INSERT INTO `hot_topic` VALUES (14, '201A6B76EE4E444FBDC91DEAC21CC2E5', '#疫情下劳动用工指南# 复工返岗，员工因担心疫情而拒绝上班，如何处理?', '上班劳动是员工在劳动合同关系中主要的义务，员工担心疫情并非不上班的 合法理由，因此，企业可依据规章制度对旷工或不服从管理安排进行违纪处罚， 严重情形的，可按照《劳动合同法》第三十九条直接予以解雇。|当然，企业有必要做好相应措施以顺得复工返岗工作，包括依法进行卫生安全防 护措施、妥善安排员工宣传沟通。', 0, 1);
INSERT INTO `hot_topic` VALUES (15, 'c331d8a3ecf1489d9303babcf5a63be7', '#疫情下劳动用工指南#复工在即，作为HR，应当提前做哪些准备？', '在复工之前的HR的一些工作：首先我们要建立一个实名制的微信群，群成员要求是部门加姓名，这个姓名一定要是全称，我们在这个群里面可以发布防御消息，利用小程序对员工进行持续的健康监测，什么叫是持续的健康监测？起码是每一天都要有。|再一个我们在这里可以发布与工作有关的通知，在这里进行防疫知识的培训，心理疏导，还有一个非常重要的功能，就是我们要制定复工之前以及复工之后的一些规章制度，且必须具备合法性。|合法性体现在三个方面，就是协商的合法性、修订的合法性以及公布的合法性。那么我们可以用实名制的工作群、工作微信群来进行这样的一个合法性的操作。|除了人事工作，还有一个就是采购工作，在复工之前采购工作也是非常重要的。一是防护用品的采购，另外就是生产物料的跟踪，由于其他的公司也同样受到疫情的一些影响，会导致他们的供货出现延迟以及价格的波动，需要采购部门及时的进行跟踪，然后把这些信息反馈给公司生产部门。生产部门在复工之前也要做好一些生产的安排。|在财务方面款项也需要及时支付，特别是防护用品的及时支付。销售部门要考虑到订单的交期，以及订单的价格变动，这些要及时跟客户进行沟通，争取能够得到客户的支持和理解。|复工前的这些工作都需要在家办公，也就是网上办公，这些也都需要注意保密和证据固化。|在复工之前，HR其实还有两个工作非常的重要，可能大家都忽略了，一个是员工调度的问题，当我们的订单量不足的时候，或者是我们其他的运营需求，不需要所有的人同时回来的情况之下，安排人员分批次开工，是人员调度工作，与其很多部门是需要进行一个协调的，这个一定要在开工之前就把工作做好。|还有一个就是员工关怀。因为在复工之后，可能会遇到有一些员工感染的情况，我们如何去做好员工关怀的工作？还有包括三期女员工的关怀，这些方面我们都要提前去做好一个预案。', 0, 0);
INSERT INTO `hot_topic` VALUES (16, 'd01f25673384444d83f26e99027d09c8', '#视频面试#什么样的自我介绍能打动面试官？', '大家都知道第一印象很重要，自我介绍就是第一印象，一份完美的自我介绍能迅速引起hr对你的兴趣，提高面试成功几率。  |3招教你做个完美的自我介绍!|1. 简述个人背景，拒绝纯背简历。不要再把简历中的工作经历和实践经验重复，HR都已经看过你的简历，这样的自我介绍非常无聊，没有亮点。|2. 表达对应聘职位的向往。即你为什么要投这个岗位? |3.证明你与应聘岗位的匹配度。这点非常重要，也是所谓的亮点。需要你仔细研究JD(招聘说明书)，提炼JD的工作内容和工作要求，然后将自己的以往经验和能力往JD靠。|个人背景+你为什么要投这个职位+你与职位的匹配度多高。 |你的自我介绍可以围绕这三大点来，具体内容再结合自身情况优化，给面试官留下好印象。', 0, 0);
INSERT INTO `hot_topic` VALUES (17, '339B4A169A3B41D291A277EAEB2C65F9', '疫情过后，2020应届毕业生该何去何从？', '我来聊聊这次疫情对2020届毕业生的冲击有多大。|1、国家政策，2月18日召开的国务院常务会议指出，当前统筹做好疫情防控和经济社会发展工作，一项迫切任务就是稳就业。|稳定就业肯定是当前重中之重的任务，但是现在因为各种原因中小企业开始倒闭了，没倒闭的企业也是在硬挺，今年注定是就业的寒冬。也就在最近国家宣布，今年专升本扩招、研究生扩招，这一方面是为了满足日益增长的高素质人才的要求，也是为了缓解今年的就业压力。|2、对于2019年下半年拿到offer的同学，我的建议是，轻易不要去放弃，最好是骑驴找马，因为一般秋招提供的岗位都是非常不错的。所以在面临极大就业压力的情况下，贸然放弃十分可惜。其实对于优秀的学生，我的建议基本上无关紧要，可以说优秀的学生，一早就完成了签约，就像我毕业那会儿11月参加国考的时候，很多准备读研究生的同学，已经就拿到了不少公司的offer，这种现象在哪一年都很常见。|3、讲真，对于我这样三本出身，甚至是二本，一本非重点专业的学生来讲，这是个寒冬。中小企业按照这样的状况肯定会减少招聘人数，大企业、国企，零星招聘，竞争激烈。针对今年2020届应届毕业的学生，我想说：及早动起来，早一刻行动，总比拖到最后实在没办法的时候再找工作，要主动的多。|如果你现在已经有实习单位，只是原本想的是拿到毕业证之后再去找新的单位，也就是毕业之后再投简历，这其实已经算是比较迟的了，你其实可以现在边实习边投简历，不用等到毕业之后拿到毕业证再找。这完全不冲突，我当时就是在一家单位实习过程中，找到的另一家，相比较之后，完成的第一次跳槽。你要是现在实习到毕业，毕业之后再找，这实习的有什么用呢？单纯地积累实习期的经验？|所以要想跳，就现在开始动起来。', 0, 0);
INSERT INTO `hot_topic` VALUES (18, 'A77A65EEB8F849A182A5559299C4B347', '#视频面试#视频面试是戴耳机好还是不戴好呀？', '你面试是看你的能力，又不是看你戴不戴耳机。你能力不行，开劳斯莱斯戴劳力士也不行。和你戴耳机有什么关系，我觉得和你前一天吃韭菜有关系', 0, 2);
INSERT INTO `hot_topic` VALUES (19, '9c168d113adc4097815764c2f95fd7e6', '#疫情下劳动用工指南#用人单位已向应聘人员发放录用通知书，可以疫情防控为由，取消录用吗？', '这个不可以。用人单位向应聘人员发出录用通知书，系其单方法律行为，对用人单位具有约束力。从诚信及用人单位公信力角度，我们不建议用人单位以此为由取消录用，一方面有违诚信原则，另一方面如果以疫情防控取消录用，可能承担缔约过失赔偿责任。为此，我们建议用人单位可以通过与应聘人员协商变更入职日期等方式解决。', 0, 0);
INSERT INTO `hot_topic` VALUES (20, '1C45F18BA4CE4E8DBB59EC470528E0F4', '#疫情下劳动用工指南#延迟复工期间，员工不服从居家办公，企业可否处罚？的否扣减工资？', '此次延迟复工为政府因防疫需求而作出的停工决定，具有强制性，企业应当遵守，居家办公的安排属于工作内容或工作方式或工作地点等临时变更，原则上应双方协商一致。 | |员工不服从企业居家办公安排应区分不同情况：因疫情防控需要，且属于本职工作，员工不服从工作安排的，企业可根据规章制度规进行处理；除此以外的，基于停工决定为强制性，若员工拒绝居家办公，企业因此而进行处罚或扣减工资是没有依据，且不符合相应的法律规定。 ', 0, 0);
INSERT INTO `hot_topic` VALUES (21, '3F3083B563DD4B00AD93F5E18A2E0C4D', '刚去面试了一家公司，工资福利待遇还好，但是入职前要签生育协议，入职的两年内不能怀孕，你们会怎么做', '19年父母保护权益新加了一条用人单位在招聘面试时不得询问女员工生育情况，你如果目前处于备孕期的话但是又不想放弃这个机会，那你可以考虑，因为这个协议签了从法律层面来说是不能生效到。但是我觉得现在用人单位来说，招一个备孕期的员工确实损失大，刚去没多久就怀孕，怀孕后工作效率肯定会下降，然后又面临休产假，盘后公司又得重新培养新人，所以对于公司来说人力成本高。这家公司要求钱此类协议，肯定之前有类似恶劣事件发生。所以你还是考虑清楚之后要面临的问题', 0, 0);
INSERT INTO `hot_topic` VALUES (22, '579fccc2ac8d4a75809dea86116de945', '男朋友对我超级好，就是不优秀也不上进，怎么办？', '我老婆跟着我几年了，一直没找到她满意的工作，家里的开销一直由我支出，之前她对我是各种要求各种要我改，直到她各种事业碰壁之后，她才意识到，普通人生活本就很不容易何必去追求那么多。我不奢求什么富贵的生活，我只希望她能找到自己喜欢的工作，也不要她能够多么强大，能够正常的幸福生活就好。', 0, 0);
INSERT INTO `hot_topic` VALUES (23, '16769B61115B4B79B686B8E8D39413B3', '#疫情下劳动用工指南#从重点疫情地区或途经重点疫情地区返岗员工的复工，企业该如何处理？', '根据《传染病防治法》相关规定，从重点疫情地区或途经重点疫情地区返回的员工，属于被隔离范围，企业可以暂时拒绝从重点疫情地区或途经重点疫情地区返回的劳动者复工返岗，要求进行医学隔离观察和采取其他必要的预防措施，员工应依法予以配合；但，在医学隔离观察期满，经确诊无风险的，企业应当接受其复工返岗。', 0, 0);
INSERT INTO `hot_topic` VALUES (24, '1514bc8eb7ba43a3a28a67479e073023', '#空中双选会#刚毕业没经验，什么都不会，面试次次不过，应届生要怎么找工作？', '给你6点建议：|1.明确自己的求职需求。你心目中理想的工作是怎样的？是否和专业相关？自己是否有相关经验？目前你要找的工作在市场上的水平和定位是怎样？你的专业能力和工作能力是否能胜任你想要的工作？jd和你能力是否匹配？你的能力相比你的同学，19届毕业生，大概处于什么水平？你要有清晰的认识。公司在招聘的时候，一个大的原则就是：找到合适的人，并让新员工对公司能够忠心一些，避免高的流动率。我们应该问自己：工作和专业相关吗？行业是重点选择哪几个？岗位的重点是做什么？我做这个有什么优势？我对自己职业的规划有什么要求？...|2.了解市场和各大公司。各个城市的竞争都很激烈，重要的是知己知彼，不管是去爬虫相关招聘网站还是和你的同学沟通求职问题，都可以帮你快速了解市场，适应社会。|3.确定先择业还是先就业。这是2条完全不同的路径，通往2条截然不同的道理。都有自己的优劣势，重点是是否知道自己要什么？比如说，一个很迷茫不清楚方向的大学生，我就建议你先去就业，适应，了解自己喜欢什么。一个很有方向感很有规划，有相关实战经验的大学生，我就建议你先择业，宁愿多花点时间找到适合的公司，逐步沟通，选择了就坚持。|4.准备好相关的简历和面试作品。在现在这个年代，光有简历是不够的，还需要有拿得出手的技能。如果没有相关硬核技能，那么面试作品就非常必要了。面试作品是反映你的一个专业能力和对面试的用心程度。面试作品分成2种：泛类面试作品和定制面试作品泛类面试作品指文章，案例，等等，就是不管你去哪家公司都可以用这份作品。定制面试作品指专门为这家公司设计，在前期做了大量的调查和研究。|5.确定是海投还是精准投放。这其实就是精细化运营还是粗放式投放的选择。我的建议是尽量精准投放，因为粗放式投放的态度是随意的，而精细化投简历是用心的，是被公司和职场所认可的，而且你的用心也会带来回报。总之，付出和收获成正比。|6.找同学一起组队找工作。可以一起建立个找工作小组，押金100元，目标1-3个月内找到工作，群内不能抱怨，只能理性分析和打气。找到工作退押金。形成一种激励。7.面试完认真总结。像我去面试，我会在面试前分析可能会遇到的问题？我应该如何回答。面试后我会总结，面试官的哪些问题是我无法回答，或者我缺乏这个能力的，下次我可以如何改进。|要注意的面试误区：|误区一：广撒网，希望多捞鱼。这种毫无选择地投简历，一来浪费你大量的时间和精力，二来即使是有结果，你也不知道这家公司或者这个职位适不适合你，因为你根本就没有仔细筛选和辨别。|误区二：面试时回答我希望通过这份工作好好学习，锻炼自己公司又不是学校，是希望你过来干活的，是价值交换，你居然准备拿我们的工作机会练手和锻炼自己 ? 我们还是找一些更靠谱的人吧。|误区三：面试时让朋友陪同，安心并自信？如果连自立都无法做到，怎么有能力胜任工作呢？|最后送大家我的寄语：每个人现在的生活状态大多是由三年前的你决定的现在不拼命，将来命玩你现在不努力，将来不给力走出自己的舒适圈，你可以亲手给自己一个更优质的未来。加油ヾ(◍°∇°◍)ﾉﾞ', 0, 0);
INSERT INTO `hot_topic` VALUES (25, '38FBE12CC5E744F1812E4C762D4F71A4', '#疫情下劳动用工指南#延迟复工期间，企业可否采取无薪休假、或补休此前加班时间，或复工后调休补班？', '依据《传染病防治法》规定“传染病暴发、流行时，县级以上地方人民政府可以采取停工、停业、停课” 和广东省人民政府《关于企业复工和学校开学时间的通知》，本次延迟复工属于政府作出的停工停业决定，依据《广东省工资支付条例》第三十九条规定，因受疫情影响导致停工停产的属于非因劳动者原因的停工停产，停工未超过一个工资支付周期（最长三十日）的，应当按照正常工作时间支付工资。 |因此，延迟复工期间企业不能采取无薪休假、或补休此前加班时间，或复工后调休补班。', 0, 0);
INSERT INTO `hot_topic` VALUES (26, '1C45F18BA4CE4E8DBB59EC470528E0F5', '#疫情下劳动用工指南# 肺炎疫情下，如何提升公司管理和沟通效率？', '每个公司方式不一样，大部分用钉钉，还是比较有效率的', 0, 0);
INSERT INTO `hot_topic` VALUES (27, '7EEB47B386584FF99AF74C16FF554A1A', '怎样才算是合格的HR', '范围太大，想要合格不容易', 0, 0);
INSERT INTO `hot_topic` VALUES (28, '9C1A34D61B1A4A90B27183D265E256AE', '#疫情下劳动用工指南#肺炎疫情时期，员工在家办公发生伤害，算工伤吗？', '根据《工伤保险条例》和司法解释相关规定，根据用人单位的安排，在家办公期间受到伤害的，认定为工伤。|工作岗位相对于工作场所而言，强调更多的不是工作的处所和位置，而是工位职责、工作任务。虽然家里算不上是工作场所，但职工在家中工作是属于完成岗位职责，在家工作处理的是其岗位职责相关的事务，发病时候履行的是工作职责范围相关的职责，那么应当属于是工伤保险条例规定的工作岗位。', 0, 0);
INSERT INTO `hot_topic` VALUES (29, 'B15782303A7647F697270554CF074211', '#疫情下劳动用工指南# 疫情防控期间，发生的劳动争议应当如何处理？', '可以通过“互联网+调解\"服务平台，获取互联网调解专家服务。对因受疫情影响不能在法定仲裁时效期间申请劳动人事争议仲裁的，仲裁时效中止。从中止时效的原因消除之日起，仲裁时效期间继续计算。因受疫情影响导致劳动人事争议仲裁机构难以按法定时限审理案件的，可相应顺延审理期限。', 0, 0);
INSERT INTO `hot_topic` VALUES (30, '3CE4F0D0614D47898B525D0E22FFEFFA', '#疫情下劳动用工指南#如果员工因患有或疑似患者而无法工作，企业是否需要支付其工资？', '对新型冠状病毒感染的肺炎患者、疑似病人、密切接触者在其隔离治疗期间或医学观察期间以及因政府实施隔离措施或采取其他紧急措施导致不能提供正常劳动的企业职工，企业应当支付职工在此期间的工作报酬，并不得依据劳动合同法第四十条、四十一条与职工解除劳动合同。但实际操作依然需要参考当地政府部门通知。', 0, 0);
INSERT INTO `hot_topic` VALUES (31, '3C0F8CA949324A2F80DEF8904FE987FD', '#疫情下劳动用工指南#武汉肺炎形势下，企业劳动用工政策有什么变化？', '首先，假期相关政策变化：|1、引导企事业单位错峰上班、居家办公和农民工分批到岗；|2、允许来自疫情高发区和非紧迫岗位人员适当延期返程；|3、国务院办公厅1月26日印发通知：延长2020年春节假期：|一、延长2020年春节假期至2月2日（农历正月初九，星期日），2月3日（星期一）起正常上班。|二、各地大专院校、中小学、幼儿园推迟开学，具体时间由教育部门另行通知。|三、因疫情防控不能休假的职工，应根据《中华人民共和国劳动法》规定安排补休，未休假期的工资报酬应按照有关政策保障落实。|其次，劳动关系政策变化：|4、人力资源社会保障部 财政部 国家卫生健康委关于因履行工作职责感染新型冠状病毒肺炎的医护及相关工作人员有关保障问题的通知：医护及相关工作人员因履行工作职责，感染新型冠状病毒肺炎或因感染新型冠状病毒肺炎死亡的，应认定为工伤，依法享受工伤保险待遇。|5、人力资源社会保障部办公厅关于妥善处理新型冠状病毒感染的肺炎疫情防控期间劳动关系问题的通知：|对肺炎患者、疑似病人、密切接触者因隔离不能提供正常劳动的企业职工，企业应当支付职工在此期间的工作报酬，并不得解除劳动合同。在此期间，劳动合同到期的，分别顺延至职工医疗期期满、医学观察期期满、隔离期期满或者政府采取的紧急措施结束。|企业因受疫情影响导致生产经营困难的，可以通过与职工协商一致采取调整薪酬、轮岗轮休、缩短工时等方式稳定工作岗位，尽量不裁员或者少裁员。', 0, 0);
INSERT INTO `hot_topic` VALUES (32, '05BA1E6E1E0246F99841B4517DE03DC9', '#疫情下劳动用工指南#延迟复工期间，企业是否可以强制要求员工提前复工？有什么后果？', '广东省人民政府依据《传染病防治法》规定“传染病暴发、流行时，县级以上地方人民政府可以采取停工、停业、停课”，而作出延迟复工的决定，具有法律约束力，除前述特殊企业，其他企业需要严格执行。 |企业若不遵从规定，提前复工、强行复工的，将依法承担如下法律后果： |（1）行政部门处罚：单位通报批评，拘留、罚款（《治安管理处罚法》第五十条）； |（2）承担刑事责任：造成疫情扩散或严重影响的，企业将会承担刑事责任（《刑法》第一百一十五条、第三百三十条）； |（3）民事赔偿责任：给他人人身、财产造成损害的，需依法承担民事责任。 |同时，企业不按规定提前复工，员工可以拒绝，企业仍需支付此期间的工资，且不能以旷工为由内部处罚或解雇。 ', 0, 0);
INSERT INTO `hot_topic` VALUES (33, 'A7B8AD745B4B4D8BA97C3937A2E821A3', '行政人力综合岗太难了', '我做了7年了', 0, 0);
INSERT INTO `hot_topic` VALUES (34, '6DEE6A86B3484E9280D70B6EEB49F03E', '#HR社区#现在是缺人还是缺工作···怎么好多面试完就不来了？有的谈的好好的，第二天就变卦了···', '大部分的人其实处于高不成，低不就的状态。有了一点工作经验，就感觉自己很优秀，找工作时心里已经给自己匹配好薪资和岗位，在面试过程中觉得还行，回家一想这里不对，哪里不对。就不去了，总而言之，还是缺少一些吃苦的精神', 0, 0);
INSERT INTO `hot_topic` VALUES (35, '4C2203EAFA6C4FD59504555BC8F9BB92', '#吐槽职场骗局#入职后发现被骗了，工资只给当初谈好的60%，我该投诉反抗？还是默默接受？', '疫情区间，情况特殊，如果是由于疫情引起的，你可以考虑自己除了这个部分外，其他的 公司发展、氛围、工作内容等是否符合你的预期，如果可以，你可以跟领导沟通一下，希望适应期结束时，也就是疫情差不多之后，可以按照约定的工资数额支付。|如果公司就是恶意为之，这类公司的“基因”基本能够决定公司不可能招到符合提升的人才，那么发展大多数情况都不会太好，那建议你选择法律途径，争取自己的合法权益。', 0, 0);
INSERT INTO `hot_topic` VALUES (36, 'BA7F93B0F8124BAFB8FE510F98055583', '去大厂还是小厂', '大厂小厂都有利弊，关键看自己的需求，旁人的看法都是为你着想，但也站在自己的立场分析的，谁也不知道你内心是怎么想的，我也只能跟你分享一下工作经验，大厂相对稳定，但事事儿多，顶住压力就能稳定不失业，小厂说开就开。总之还要自己衡量，实在难以决断，就先去大厂磨练，实在顶不住了再去小厂发展，多一次经历多一些长进嘛。', 0, 0);
INSERT INTO `hot_topic` VALUES (37, 'ee6da686feb3455dbeab9272b6463ef4', '#视频面试#疫情未停在家求职，该如何准备视频面试？', '1、了解视频面试的有效交流成分|对于视频面试，我们要了解有效交流的成分都有哪些。根据科学研究得出，视频面试有效的交流55%来自于面部和身体语言，38％来自于语音语调，7%来自于说话内容。| |在视频中，由于屏幕和摄像镜头的角度不同，盯着屏幕面试官则无法跟你进行眼神交流，容易产生距离感；其次，如果身体一直定住不动，容易显得自己很呆板；最后面试者在语音语调上，声调过尖或语速很快，对方可能不会感舒适。| |因此，面试者可先试演盯着摄像头说话，让对方有一种面谈的感觉；增加一些无伤大雅的微动作，比如点头赞同对方；以及找到自己最适合视频说话的语调和语速，这些将会缩小与面试官的距离感。|2.个人的形象准备|虽然是线上的视频面试，但还是可以看到彼此，我们都需要做好准备。比如面试官在国外的下午进行视频面试，国内刚好是晚上，如果此时一身家居服的你与面试官视频，对方难以感受到尊重。所以，无论任何时间点，符合面试的正式服装并且穿戴整齐，才能将专业度传递给面试官。|3.室内场所的选择|选择一个安静的没有干扰的地方，视频区域整洁没有多余的杂物；灯光明亮，避免人像曝光，面试官可清晰看到你；确保坐的椅子舒适，利于自己在面试过程中精神保持专注。|4.个人设备和网络|确认手机电量充足，对应的相机和麦克风功能可以正常使用；关闭任何会发出提示音的设备，避免面试中收到干扰；测试设备和网络是否能正常使用，减少面试中出现断网等低级错误。| |疫情未止，但这不会成为找工作面试的阻碍，在疫情期间做好面试的充足准备，提高线上面试的重视度，即便现场出现突发状况，镇静并且及时与对方沟通，商量解决方案，一切都能迎刃而解。| |总之，只要做好十足的准备，确保一切都是最佳状态，即便从未经历过视频面试的你，也能脱颖而出。', 0, 0);
INSERT INTO `hot_topic` VALUES (38, 'A77DEA69CE8F4BCB8F4D988EFC6C7057', '总是被面试者放鸽子。。。', '投简历的公司一个都不招聘，不投的公司还找你，真不知道这是啥招聘！', 0, 0);
INSERT INTO `hot_topic` VALUES (39, '15ED7278CBC54984844F500E01625E55', '因疫情二月整月未上班，三月才复工，二月份工资如何安排？', '看公司的情况吧，我们公司是有上班的', 0, 0);
INSERT INTO `hot_topic` VALUES (40, 'C49A3EFDF3F04CF291E4B6F4571A0282', '#简历估薪#疫情期间被公司辞退。投了很多简历也没有几个面试的。想找双休的工作好难', '双休的确实挺难，单休的超级多', 0, 0);
INSERT INTO `hot_topic` VALUES (41, '34843998C0FF4CEC82722F2BF658387D', '双休在哪里？五险一金在哪里？', '不是五险一金和双休的公司你是特马的耍流氓', 0, 0);
INSERT INTO `hot_topic` VALUES (42, '4534D60F518547348ED1AB541F9E850B', '#疫情下劳动用工指南#如果在员工治疗或者隔离期间劳动合同恰好到期，应如何处理？', '根据规定，在此期间企业不能够直接终止劳动合同，而是应将劳动合同顺延至医疗期、隔离期或政府采取紧急措施结束的时期。', 0, 0);
INSERT INTO `hot_topic` VALUES (43, 'C7346E863E5546748EB28D5A237DCB36', '#HR提问墙#年前辞职，想去北京工作，现在网上投简历基本零回复，应该怎么办呢？求指导方向', '你问怎么办，以下个人建议，仅供参考！|1、你要明白这次疫情不知道什么时候结束，钟南山院士估计4月底全面控制，那就按照4月底的话。在5月前，大部分企业没办法正常开工，即使复工的企业，也大部分在亏损，不裁员已非常好了，比较少会去招人！|2、如果你能耐得住，家里有钱，那就在家好好学习提高自己或者在家玩吧。|3、如果经济比较不宽裕，那在你当地找个工作过度吧，你现在去北京还要找房子，找工作。而且现在特殊期间，房子，工作都不好找！', 0, 0);
INSERT INTO `hot_topic` VALUES (44, '2B16647BC8E64B66A2DB58D7365501EA2', '#HR社区#初创公司，各种渠道招聘，老板各种不满意，看不上，发offer反悔，来俩月就让人走', '庙太大，我走人|实际这是一个很普遍现象，初创公司行政架构，各部门职责未完善，没有完善的流程管理，老板的结果论造成各种不满意|初创公司很多职位一岗复用，达到老板要求综合能用，老板觉得就这样，又有更高要求，要求不断提高，也出现各种不满意，最后要么人自己走，要么老板看不顺眼走人|初创公司老板自己需要配备什么样自己也不清楚，面试各种不满意，招来人自己不顺眼又辞掉，各种变数，HR工作绩效最后是零，也面临换人。|我曾经供职一家初创公司，老板六个月会换一次血从副总裁高层到部门经理，他的理解就是不定期换人会增加企业运营动力。', 0, 0);
INSERT INTO `hot_topic` VALUES (45, '1514bc8eb7ba43a3a28a67479e073023', '#空中双选会#应届毕业生第一份工作是进国企好？还是进私企好点？', '第一份工作，最好是国企。为什么？因为国企只招应届生。这个应届生身份卡死多少往届在私企不得志的人。这个社会精英是少的。绝大多数去私企，说白了，就是送人头。如果进华为这种大企业，还是值得的。但是又有多少人能进私企大企业的？很多人，在小公司的小私企打工，各种保障不健全啊，苦苦追寻的五险一金在何方？都是个问题。', 0, 0);
INSERT INTO `hot_topic` VALUES (46, '3C0F8CA949324A2F80DEF8904FE987FD', '#疫情下劳动用工指南#年度述职、考评计划在春节之后进行，结果将影响年度调薪，疫情期间应如何操作？', '从目前来看，疫情防控还需要持续一段时间，不合适聚集，企业可以考虑采用视频、电话会议、网络会议的方式进行，非常之日，当行非常之策。', 0, 0);
INSERT INTO `hot_topic` VALUES (47, '190BF6A794904E9BB3D6A28CCE881F1A', '同事找你借钱，你借还是不借？', '不会借|你好心借给他，但是人家可没你那么好心。同事说家里人生病借钱，借了三万多给他，说好的每个月还一点的三年内还清还写了欠条身份证复印件都有。现实是他网络赌博缺钱，全输了。现在呢不破罐子破摔，一年都快过去了就还了一次几百块，提醒他吧，就是装，还说时限一到肯定还。而且不止我一个借了钱，其他几个同事最多10w最少1w一共25w。看他这样子是真的不会还了。时间一到法院见吧。', 0, 0);
INSERT INTO `hot_topic` VALUES (48, '1B8708E00EC347A5816750BE29F40522', '要不要换工作，生活压力很大，要还房贷要结婚，这段期间要不要放弃高工资选择更有前景的机会。', '新公司看中你的证，说明你的证值钱啊，应该好好用这个证才对。 既然压力这么大为何还要放弃高工资呢，埋头苦干多攒钱才是王道。 建议别辞职接着干，然后好好用这个证，踏实结婚踏实攒钱。', 0, 0);
COMMIT;

-- ----------------------------
-- Table structure for invite_interview
-- ----------------------------
DROP TABLE IF EXISTS `invite_interview`;
CREATE TABLE `invite_interview` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `invite_id` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL,
  `unionid` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL,
  `invite_user_id` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL,
  `invite_time` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `invite_company` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,
  `invite_job` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `invite_addr` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `invite_remark` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `status` int(3) DEFAULT NULL,
  `update_time` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
-- Table structure for jobs
-- ----------------------------
DROP TABLE IF EXISTS `jobs`;
CREATE TABLE `jobs` (
  `job_id` varchar(40) COLLATE utf8_unicode_ci NOT NULL,
  `company_id` varchar(40) COLLATE utf8_unicode_ci DEFAULT NULL,
  `job_name` varchar(60) COLLATE utf8_unicode_ci DEFAULT NULL,
  `job_type` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `empl_type` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `working_exp` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `welfare` varchar(120) COLLATE utf8_unicode_ci DEFAULT NULL,
  `welfare_all` varchar(120) COLLATE utf8_unicode_ci DEFAULT NULL,
  `city` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `display` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `salary` varchar(40) COLLATE utf8_unicode_ci DEFAULT NULL,
  `skill_require` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `other_require` varchar(1400) COLLATE utf8_unicode_ci DEFAULT NULL,
  `job_address` varchar(300) COLLATE utf8_unicode_ci DEFAULT NULL,
  `recruit` varchar(8) COLLATE utf8_unicode_ci DEFAULT NULL,
  `publisher_id` varchar(30) COLLATE utf8_unicode_ci DEFAULT 'onmaV07KFbndEt5JwBXCuIlc-cvo',
  `edu_level` varchar(8) COLLATE utf8_unicode_ci DEFAULT NULL,
  `update_date` varchar(40) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`job_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

